import React, {useEffect} from 'react';
import {NavLink} from "react-router-dom";
import useFetch from "hooks/useFetch";
import UserArticles from "pages/userProfile/components/userArticles";


const UserProfile = ({location, match}) => {
    const slug = match.params.slug
    const isFavorites = location.pathname.includes('favorites')
    const apiUrl = `/profiles/${slug}`
    const [{response}, doFetch] = useFetch(apiUrl)

    useEffect(() => {
        doFetch()
    }, [doFetch])

    if (!response) {
        return null
    }

    return (
        <div className='profile-page'>
            <div className="user-info">
                <div className="container">
                    <div className="row">
                        <div className="col-xs-12 col-md-10 offset-md-1">
                            <img className="user-img"
                                 src={response.profile.image}/>
                            <h4>{response.profile.username}</h4>
                            <p>{response.profile.bio}</p>
                            <button className="btn btn-sm action-btn btn-outline-secondary"><i
                                className="ion-plus-round"></i> &nbsp; Follow testingFox
                            </button>
                            <a className="btn btn-sm btn-outline-secondary action-btn" hidden="" href="/settings">
                                <i className="ion-gear-a"></i> Edit Profile Settings </a>
                        </div>
                    </div>
                </div>
            </div>
            <div className="container">
                <div className="row">
                    <div className="col-xs-12 col-md-10 offset-md-1">
                        <div className="articles-toggle">
                            <ul className="nav nav-pills outline-active">
                                <li className="nav-item">
                                    <NavLink
                                        exact
                                        className="nav-link"
                                        to={`/profiles/${response.profile.username}`}>
                                        My Posts
                                    </NavLink>
                                </li>
                                <li className="nav-item">
                                    <NavLink
                                        className="nav-link"
                                        to={`/profiles/${response.profile.username}/favorites`}>
                                       Favorites Posts
                                    </NavLink>
                                </li>
                            </ul>
                        </div>
                        <UserArticles username={response.profile.username} location={location}
                                      isFavorites={isFavorites}
                        match={match}/>
                    </div>
                </div>
            </div>
        </div>

    )
}

export default UserProfile;